// ./src/App.js

import React, { useState } from 'react';
import './App.css';
import { computerVision, isConfigured as ComputerVisionIsConfigured } from './azure-cognitiveservices-computervision';

function App() {

  const [fileSelected, setFileSelected] = useState('');
  const [description, setDescription] = useState('');
  const [processing, setProcessing] = useState(false);

  // Handle URL input change
  const handleChange = (e) => {
    setFileSelected(e.target.value);
  }

  // Function to call the Azure API and fetch description
  const onFileUrlEntered = () => {
    if (!fileSelected) {
      alert("Por favor, insira uma URL de imagem.");
      return;
    }

    // Hold UI during processing
    setProcessing(true);
    setDescription('');

    // Call Azure Computer Vision service
    computerVision(fileSelected)
      .then((response) => {
        if (response && response.description) {
          // Verifique se a resposta possui captions
          const captions = response.description.captions;
          if (captions && captions.length > 0) {
            setDescription(captions[0].text);
          } else {
            setDescription('Nenhuma descrição encontrada para a imagem.');
          }
        } else {
          setDescription('Nenhuma descrição encontrada para a imagem.');
        }
        setProcessing(false);
      })
      .catch((error) => {
        console.error('Erro ao buscar a descrição da imagem:', error.message);
        console.error('Erro completo:', error);
        setDescription('Erro ao obter a descrição da imagem.');
        setProcessing(false);
      });
  };

  // Function to render the UI
  const Analyze = () => {
    return (
      <div style={{ textAlign: 'center', padding: '50px' }}>
        <h1>Azure Vision - Descrição da Imagem</h1>
        <div>
          <input
            type="text"
            placeholder="Digite a URL da imagem"
            size="50"
            value={fileSelected}
            onChange={handleChange}
            style={{ padding: '10px', fontSize: '16px' }}
          />
        </div>
        <button
          onClick={onFileUrlEntered}
          style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
          disabled={processing}
        >
          {processing ? 'Processando...' : 'Obter Descrição'}
        </button>
        <div style={{ marginTop: '20px', fontSize: '18px' }}>
          {description && <p><strong>Descrição da Imagem:</strong> {description}</p>}
        </div>
      </div>
    );
  };

  // If Azure service is not configured properly, show an error
  const CantAnalyze = () => {
    return (
      <div>Chave e/ou endpoint não estão configurados em ./azure-cognitiveservices-computervision.js</div>
    );
  };

  // Determine what to render based on configuration
  function Render() {
    const ready = ComputerVisionIsConfigured();
    if (ready) {
      return <Analyze />;
    }
    return <CantAnalyze />;
  }

  return (
    <div>
      {Render()}
    </div>
  );
}

export default App;
